\n      &lt;div className=\"row\">\r\n        &lt;div className=\"col-md-8\">\r\n          &lt;div className={styles.items}>\r\n            {posts?.length > 0 &amp;&amp;\r\n              posts?.map((post) => {\r\n                return &lt;Post key={post.slug} post={post} styles={styles} />;\r\n              })}\r\n          &lt;/div>\r\n        &lt;/div>\r\n        &lt;div className=\"col-md-4\">\r\n          &lt;Sidebar categories={categories} styles={styles} />\r\n        &lt;/div>\r\n      &lt;/div>\r\n    &lt;/div>\r\n  );\r\n};\r\nexport default Blog;\r\nexport async function getStaticProps() {\r\n  const data = await getPostList();\r\n  const categories = await getCategorySlugs();\r\n\n  return {\r\n    props: {\r\n      posts: data.nodes,\r\n      categories,\r\n    },\r\n  };\r\n}\r\n</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:paragraph -->\n<p>[slug.js] for post</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>import Image from \"next/image\";\nimport Toc from \"@/components/tableofcontent\";\nimport styles from \"../../styles/blog.module.css\";\n\n\nimport { getCategorySlugs, getPostList, getSinglePost } from \"lib/posts\";\nimport { useEffect } from \"react\";\nconst Blog = ({ post, categories }) => {\n\n  return (\n    &lt;>\n      {post &amp;&amp; (\n        &lt;main className={`${styles.blog} mainscrollbar mb-4`}>\n          &lt;h1 className=\"p-2\">{post?.title}&lt;/h1>\n          &lt;div className=\"container\">\n            &lt;div className=\"row\">\n              &lt;div className=\"col-md-12\">\n                &lt;div\n                  className={`my-4 article ${styles.article}`}\n                  dangerouslySetInnerHTML={{ __html: post.content }}\n                />\n              &lt;/div>\n            &lt;/div>\n          &lt;/div>\n        &lt;/main>\n      )}\n    &lt;/>\n  );\n};\nexport default Blog;\n\nexport async function getStaticPaths() {\n  const { nodes: posts } = await getPostList();\n  const paths = posts.map((post) => ({\n    params: { slug: post.slug },\n  }));\n  return {\n    paths,\n    fallback: true,\n  };\n}\nexport async function getStaticProps(req) {\n  const post = await getSinglePost(req.params.slug);\n  if (!post)\n    return {\n      notFound: true,\n    };\n  return {\n    props: {\n      post,\n    },\n    revalidate: 10,\n  };\n}\n</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:heading -->\n<h2>how to query posts from WordPress by graphql</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>we can use WordPress rest API to query posts from WordPress by graphql</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Query for posts</h3>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>  query: `query getMorePosts {\r\n        posts {\r\n          nodes {\r\n            date\r\n            excerpt(format: RENDERED)\r\n            slug\r\n            title\r\n            featuredImage {\r\n              node {\r\n                mediaDetails {\r\n                  file\r\n                  sizes {\r\n                    file\r\n                    sourceUrl\r\n                    width\r\n                    height\r\n                  }\r\n                }\r\n              }\r\n            }\r\n            categories {\r\n              nodes {\r\n                name\r\n                slug\r\n              }\r\n            }\r\n          }\r\n          pageInfo {\r\n            endCursor\r\n            hasNextPage\r\n            hasPreviousPage\r\n            startCursor\r\n          }\r\n        }\r\n      }`,</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Query for single post</h3>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>    query: `query getSinglePost {\r\n      postBy(slug: \"${slug}\") {\r\n        content\r\n        date\r\n        excerpt\r\n        featuredImage {\r\n          node {\r\n            mediaDetails {\r\n              sizes {\r\n                file\r\n                sourceUrl\r\n                height\r\n                width\r\n              }\r\n            }\r\n          }\r\n        }\r\n        modified\r\n        slug\r\n        title\r\n      }\r\n\r\n    }`,</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Query for categorylist</h3>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>     `query getAllCategorySlug {\r\n                categories {\r\n                  nodes{\r\n                    slug\r\n                  }\r\n                }\r\n            }`</code></pre>\n<!-- /wp:code -->', 'How to make a blog with next js and WordPress as headless cms with graphql', '', 'inherit', 'closed', 'closed', '', '42-revision-v1', '', '', '2023-02-03 19:45:45', '2023-02-03 19:45:45', '', 42, 'https://cms.frontendzone.com/?p=53', 0, 'revision', '', 0);
